// Delete deletes a single NVD record with an executor.
// Delete will match against the primary key column to find the record to delete.
func (o *NVD) Delete(exec boil.Executor) error {
	if o == nil {
		return errors.New("models: no NVD provided for delete")
	}

	if err := o.doBeforeDeleteHooks(exec); err != nil {
		return err
	}

	args := queries.ValuesFromMapping(reflect.Indirect(reflect.ValueOf(o)), nvdPrimaryKeyMapping)
	sql := "DELETE FROM \"nvds\" WHERE \"id\"=$1"

	if boil.DebugMode {
		fmt.Fprintln(boil.DebugWriter, sql)
		fmt.Fprintln(boil.DebugWriter, args...)
	}

	_, err := exec.Exec(sql, args...)
	if err != nil {
		return errors.Wrap(err, "models: unable to delete from nvds")
	}

	if err := o.doAfterDeleteHooks(exec); err != nil {
		return err
	}

	return nil
}

