// DeleteAll deletes all matching rows.
func (q nvdQuery) DeleteAll() error {
	if q.Query == nil {
		return errors.New("models: no nvdQuery provided for delete all")
	}

	queries.SetDelete(q.Query)

	_, err := q.Query.Exec()
	if err != nil {
		return errors.Wrap(err, "models: unable to delete all from nvds")
	}

	return nil
}

